(function($) {

    var scope = $('#campaign-user-selection'),
        form = $('form', scope),
        aBlocks = $('.action-block', scope);

    aBlocks.on('touchstart', function(e) {
        $(this).toggleClass('active');
    });

    // Prevent default form submission
    //form.on('submit', function(e) { e.preventDefault(); console.log(form.serialize()); });

    // Custom form submission based on user action
    $('button', form).on('click', function(e) {
        $('input[name="campaign"]', form).val($(this).data('campaign'));
    });

})(jQuery);